import React, {useState} from 'react';
import './tile.scss';

function Tile(props) {
    const [cover, setCover] = useState('cover');

    function selectTile(item) {
        if(item === -1) alert("You lose!");
        setCover('un-cover')
    }

    return (
                <div className={`tile ${cover} ${index}`} onClick={() => selectTile(item)}>
                    {item}
                </div>
    )
}
export default Tile;
