import React, {useState, useEffect, useRef} from 'react';
import './tile.scss';

function Tile(props) {
    // const [cover, setCover] = useState('cover');
    const tileEl = useRef(null);
    // let currentTile = tileEl.current;
    // currentTile = `${props.rowIndex},${props.index}`;

    function selectTile(item) {
        if(item === -1) alert("You lose!");
        else {

            // setCover('un-cover');
            // console.log(props.rowIndex);
            tileEl.current.classList.add("un-cover");
            console.log(tileEl.current);
        }
    }
    useEffect(() => {
        // tileEl.current = currentTile;

        // console.log(tileEl.current);
    });

    return (
        <div ref={tileEl} className={`tile cover` } onClick={() => selectTile(props.tile)}>
            {props.tile}
        </div>
    )
}
export default Tile;
