import React, {useState, useEffect, useRef} from 'react';
import './tile.scss';

function Tile(props) {
    const [cover, setCover] = useState('cover');
    let tileEl = useRef(null);
    let currentTile = `${props.index}`;

    function selectTile(item) {
        if(item === -1) alert("You lose!");
        else {

            setCover('un-cover');
            console.log(props.rowIndex);
            console.log(tileEl);
        }
    }
    useEffect(() => {
        tileEl = currentTile;
    });

    return (
        <div ref={tileEl} className={`tile ${cover}` } onClick={() => selectTile(props.tile)}>
            {props.tile}
        </div>
    )
}
export default Tile;
