        function clearTiles(row, col) {
            for(let i = -1; i < 2; i++) {
                if(props.tileArr[row-1][col+i] !== undefined && props.tileArr[row-1][col+i] !== -1) {replaceShowingTiles.push(`${row-1}${col+i}`)};
                if(props.tileArr[row][col+i] !== undefined && props.tileArr[row][col+i] !== -1) {replaceShowingTiles.push(`${row}${props.index+i}`)};
                if(props.tileArr[row+1][col+i] !== undefined && props.tileArr[row+1][col+i] !== -1) {replaceShowingTiles.push(`${row+1}${props.index+i}`)};
            }
        }
        props.onupdateShowingTiles(replaceShowingTiles);
    }

    return (
        <div className={ props.showingTiles.filter(tile => tile == `${props.rowIndex}${props.index}`).length > 0 ? `un-cover tile ${props.rowIndex}${props.index}` : `cover tile ${props.rowIndex}${props.index}` } onClick={() => selectTile(props.tile)}>
            {props.tile}
        </div>
    )
}

const mapStateToProps = state => ({
    showingTiles: state.showingTiles,
});

const mapActionsToProps = {
    onupdateShowingTiles: updateShowingTiles,
};

export default connect(mapStateToProps, mapActionsToProps) (Tile);



