import React, {useState, useEffect, useCallback, useRef} from 'react';
import { updateShowingTiles } from '../../actions/tile-action';
import { connect } from 'react-redux';

import './tile.scss';

function Tile(props) {
    // const [cover, setCover] = useState('cover');
    // const itemsRef = useRef([]);
    // let currentTile = itemsRef.current;
    // currentTile = `${props.rowIndex},${props.index}`;

    function selectTile(item) {
        let replaceShowingTiles = [...props.showingTiles];
        replaceShowingTiles.push(`${props.rowIndex}${props.index}`);
        replaceShowingTiles.push(`${props.rowIndex}${props.index}`);

        props.onupdateShowingTiles(replaceShowingTiles);
        if(item === -1) alert("You lose!");

        else if (item === null) {
            console.log(item);
            console.log(`${props.rowIndex}${props.index}`);
            // itemsRef.current[props.rowIndex + (props.index*10)].classList.add("un-cover");
            // itemsRef.current[10].classList.add("un-cover");

            // setCover('un-cover');
            // console.log(props.rowIndex);
            // itemsRef.current.classList.add("un-cover");

        }
    }

    return (
        <div className={ props.showingTiles.filter(tile => tile == `${props.rowIndex}${props.index}`).length > 0 ? `un-cover tile ${props.rowIndex}${props.index}` : `cover tile ${props.rowIndex}${props.index}` } onClick={() => selectTile(props.tile)}>
            {props.tile}
        </div>
    )
}

const mapStateToProps = state => ({
    showingTiles: state.showingTiles,
});

const mapActionsToProps = {
    onupdateShowingTiles: updateShowingTiles,
};

export default connect(mapStateToProps, mapActionsToProps) (Tile);
