import React, {useState} from 'react';
import './tile.scss';

function Tile(props) {
    const [cover, setTiles] = useState('cover');

    useEffect(() => {
        props.tile.forEach(item => {
            this[`${item}`] = React.createRef()
        })
    })
    function selectTile(item) {
        if(item === -1) alert("You lose!");

    }

    return (
        <div>
            {props.tile.map((item, index) => (
                <div className={`tile ${cover}`} onClick={() => selectTile(item)}>
                    {item}
                </div>
            ))}
        </div>
    )
}
export default Tile;
