import React from 'react';
import './tile.scss';

function Tile(props) {
    // props.tile.forEach(item => {
    //     this[`${item}`] = React.createRef()
    // })
    function selectTile(item) {
        if(item === -1) alert("You lose!");
        this[`${item}`].setAttribute("style", "color: white");
    }

    return (
        <div>
            {props.tile.map((item, index) => (
                <div key={item} ref={this[`${item}`]} className="tile cover" onClick={() => selectTile(item)}>
                    {item}
                </div>
            ))}
        </div>
    )
}
export default Tile;
