import React, {useState} from 'react';
import './tile.scss';

function Tile(props) {
    const [cover, setCover] = useState('cover');
    tileRef = React.createRef();
    function selectTile(item) {
        if(item === -1) alert("You lose!");
        setCover('un-cover');
        console.log(this.tileRef);
    }

    return (
        <div ref={tileRef} className={`tile ${cover}` } onClick={() => selectTile(props.tile)}>
            {props.tile}
        </div>
    )
}
export default Tile;
