import React, { useState } from 'react';
import './board.scss';

import Tile from '../tile/tile';

function Board(props) {
    const [tiles, setTiles] = useState([[], [], []]);
    console.log(tiles);
    function genTiles() {
        let replaceTiles = [...tiles];
        replaceTiles.forEach(arr => {
            arr.forEach(item => {
                let num = Math.floor(Math.random() * (10 + 1)) -1;
                if(num === -1) item = num;
            })
        })
        setTiles(replaceTiles);
        console.log(tiles)
    }
    return (
        <div className="board">
            <div className="board__message">Game in progress</div>
            <button onClick={genTiles}>Start Game</button>
            <div className="board__tiles">
                {tiles.map((tile, index) => (
                    <Tile key={index} tile={tile}/>
                ))}
            </div>
        </div>
    )
}

export default Board;
